const imgs=document.querySelectorAll(".image img");imgs.forEach(e=>{let t=e.dataset.src,i=Math.round(e.clientWidth*devicePixelRatio),a=Math.round(e.clientHeight*devicePixelRatio);e.src=t.replace("width",i).replace("height",a),e.addEventListener("load",()=>{e.parentElement.classList.remove("loading")})});